
public class teste12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int valor1, valor2;
		int resultado1, resultado2, resultado3;
		int resultado4, resultado5;
		
		valor1=23;
		valor2=12;
		
		resultado1 = valor1 + valor2;
		resultado2 = valor1 - valor2;
		resultado3 = valor1 * valor2;
		resultado4 = valor1 / valor2;
		resultado5 = valor1 % valor2;
		
		System.out.println(" A soma dos valores é:" + resultado1);
		System.out.println(" A subtração dos valores é:"+ resultado2);
		System.out.println(" A multiplicação dos valores é :"+ resultado3);
		System.out.println(" A divisão dos valores é:"+ resultado4);
		System.out.println(" O resto da divisão dos valores é" + resultado5);
		
	}

}
